(89, 0, 'rss_a5420c83891a9c88ad2a4f04584a5efc', 'O:9:"MagpieRSS":17:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:33:"uberdose on "All in One SEO Pack"";s:4:"link";s:65:"http://wordpress.org/extend/plugins/all-in-one-seo-pack/#post-753";s:7:"pubdate";s:31:"Fri, 30 Mar 2007 20:08:18 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"uberdose";}s:4:"guid";s:40:"753@http://wordpress.org/extend/plugins/";s:11:"description";s:92:"Automatically optimizes your Wordpress blog for Search Engines (Search Engine Optimization).";s:7:"summary";s:92:"Automatically optimizes your Wordpress blog for Search Engines (Search Engine Optimization).";}i:1;a:7:{s:5:"title";s:27:"donncha on "WP Super Cache"";s:4:"link";s:61:"http://wordpress.org/extend/plugins/wp-super-cache/#post-2572";s:7:"pubdate";s:31:"Mon, 05 Nov 2007 11:40:04 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"donncha";}s:4:"guid";s:41:"2572@http://wordpress.org/extend/plugins/";s:11:"description";s:73:"A very fast caching engine for WordPress that produces static html files.";s:7:"summary";s:73:"A very fast caching engine for WordPress that produces static html files.";}i:2;a:7:{s:5:"title";s:29:"alexrabe on "NextGEN Gallery"";s:4:"link";s:62:"http://wordpress.org/extend/plugins/nextgen-gallery/#post-1169";s:7:"pubdate";s:31:"Mon, 23 Apr 2007 20:08:06 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"alexrabe";}s:4:"guid";s:41:"1169@http://wordpress.org/extend/plugins/";s:11:"description";s:102:"NextGEN Gallery is a full integrated Image Gallery plugin for WordPress with a Flash slideshow option.";s:7:"summary";s:102:"NextGEN Gallery is a full integrated Image Gallery plugin for WordPress with a Flash slideshow option.";}i:3;a:7:{s:5:"title";s:30:"momo360modena on "Simple Tags"";s:4:"link";s:58:"http://wordpress.org/extend/plugins/simple-tags/#post-2463";s:7:"pubdate";s:31:"Wed, 10 Oct 2007 15:30:11 +0000";s:2:"dc";a:1:{s:7:"creator";s:13:"momo360modena";}s:4:"guid";s:41:"2463@http://wordpress.org/extend/plugi